import React, { useState,useEffect } from "react";
import "../transactions/transaction.css";
import Paginate from "../global/Paginate";
import tranData from "../../data/transaction.json";

import AddTransaction from "./AddTransaction";
import Modal from "../global/Modal";

import axios from "axios";
import {BASE_URL}from "../../util/constants"



function Transaction() {
  const total = 15;
  const [transactions, setTransactions] = useState();

  const [isLoading,setLoading]=useState(true)

  const loadData=async()=>{
    try{
      setLoading(true)
      const res = await axios.get(`${BASE_URL}/transaction/`)
      setTransactions(res.data.data)
      setLoading(false)


    }catch(err){
      console.log(err)
      // alert("Something went wrong")
    }
  }
  useEffect(()=>{
    loadData()
  },[])




  return (
    <div>
      <div className="tran-header">
        <p>Transaction</p>
      </div>
      <div>
        <div className="tran-add">
          <button
            onClick={() => {
              setIsopen(true);
            }}
          >
            Add Transaction
          </button>
        </div>

        <div>
          <Modal trigger={isopen} setTrigger={setIsopen}>
            <AddTransaction trigger={isopen} setTrigger={setIsopen} />
          </Modal>
        </div>
      </div>
      <div className="tran-title">
        <p>Name</p>
        <p>Category</p>
        <p>Amount</p>
        <p>Inward/Outward</p>
        <p>Date</p>
        <p>Buyer Name</p>
        <p>Buyer Number</p>
      </div>
      {displayTranList}
      <div className="pagination">
        <div className="show-result">
          <p>
            Showing Result {pageVisited + 1} to{" "}
            {/* {transactionsPerPage + pageVisited} of {total} */}
          </p>
        </div>
        <div className="pageNumber">
          {/* <Paginate pageChange={pageChange} pageCount={pageCount} /> */}
        </div>
      </div>
    </div>
  );
}

export default Transaction;
