import React,{useState,useEffect} from "react";
import "./dashboard.css";
import axios from "axios";
import {BASE_URL}from "../../util/constants"


function Dashboard() {

  const [data,setData]=useState([]);
  const [isLoading,setLoading]=useState(true)

  const loadData=async()=>{
    try{
      setLoading(true)
      const res = await axios.get(`${BASE_URL}/transaction/report`)
      setData(res.data.data)
      setLoading(false)


    }catch(err){
      console.log(err)
      // alert("Something went wrong")
    }
  }
  useEffect(()=>{
    loadData()
  },[])

  if(isLoading){
    return(<p>....Loading</p>)
  }
  return (
    <div>
      {data.map((dashDetail, index) => {
        return (
          <div key={index}>
            <div className="dash-card">
              <div className="t-title">
                {" "}
                <p>{dashDetail.totalCredit} Overview</p>
              </div>

              <div className="d-hr"></div>
              <div className="t-detail">
                <div className="t-cost t-card">
                  {" "}
                  <div className="t-img">
                    <img src="taka1.png" alt="taka" />
                  </div>
                  <div className="t-info">
                    <h3>Total Cost</h3>
                    <p>{dashDetail.totalCost} Taka</p>
                  </div>
                </div>
                <div className="t-revenue t-card">
                  {" "}
                  <div className="t-img">
                    <img src="taka2.png" alt="taka" />
                  </div>
                  <div className="t-info">
                    <h3>Total Revenue</h3>
                    <p>{dashDetail.totalRevenue} Taka</p>
                  </div>
                </div>
                <div className="t-profit t-card">
                  {" "}
                  <div className="t-img">
                    <img src="taka3.png" alt="taka" />
                  </div>
                  <div className="t-info">
                    <h3>Total Profit</h3>
                    <p>{dashDetail.totalProfit} Taka</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default Dashboard;
