      //       <p>{landDetails.start}</p>
      //       <p>{landDetails.due}</p>
      //     </div>
      //   </div>
      // );


  return (
    <div>
      <div>
        <div className="land-header">
          <p>Lands</p>
        </div>
        <div>
          <div className="land-add">
            <button
              onClick={() => {
                setIsopen(true);
              }}
            >
              Add Land
            </button>
          </div>

          <div>
            <Modal trigger={isopen} setTrigger={setIsopen}>
              <AddLand trigger={isopen} setTrigger={setIsopen} />
            </Modal>
          </div>
        </div>
        <div className="land-title">
          <p>Name</p>
          <p>Quantity</p>
          <p>Amount</p>
          <p>Start date</p>
          <p>Due Date</p>
        </div>

      </div>
    </div>
  );
}

export default Land;
