import React, { useState ,useEffect} from "react";

import "../transactions/transaction.css";

import Paginate from "../global/Paginate";
import tranData from "../../data/transaction.json";

import AddTransaction from "./AddTransaction";
import Modal from "../global/Modal";


import axios from "axios";
import {BASE_URL}from "../../util/constants"



function Transaction() {
  const total = 15;
  const [transactions, setTransactions] = useState(tranData.slice(0, total));
  const [pageNumber, setPageNumber] = useState(0);
  const [isLoading,setLoading]=useState(true)


  const transactionsPerPage = 6;
  const pageVisited = transactionsPerPage * pageNumber;


  const loadData=async()=>{
    try{
      setLoading(true)
      const res = await axios.get(`${BASE_URL}/transaction/report`)
      setData(res.data.data)
      setLoading(false)


    }catch(err){
      console.log(err)
      // alert("Something went wrong")
    }
  }
  useEffect(()=>{
    loadData()
  },[])



  const displayTranList = transactions
    .slice(pageVisited, pageVisited + transactionsPerPage)
    .map((tranDetails, index) => {
      return (
        <div key={index}>
          <div className="trandetails">
            <p>{tranDetails.Name}</p>
            <p>{tranDetails.category}</p>
            <p>{tranDetails.amount}</p>
            <p>{tranDetails.inout}</p>
            <p>{tranDetails.date}</p>
            <p>{tranDetails.buyerName}</p>
            <p>{tranDetails.buyerNumber}</p>
          </div>
        </div>
      );
    });

  const pageCount = Math.ceil(transactions.length / transactionsPerPage);
  const pageChange = ({ selected }) => {
    setPageNumber(selected);
  };

  const [isopen, setIsopen] = useState(false);

  return (
    <div>
      <div className="tran-header">
        <p>Transaction</p>
      </div>
      <div>
        <div className="tran-add">
          <button
            onClick={() => {
              setIsopen(true);
            }}
          >
            Add Transaction
          </button>
        </div>

        <div>
          <Modal trigger={isopen} setTrigger={setIsopen}>
            <AddTransaction trigger={isopen} setTrigger={setIsopen} />
          </Modal>
        </div>
      </div>
      <div className="tran-title">
        <p>Name</p>
        <p>Category</p>
        <p>Amount</p>
        <p>Inward/Outward</p>
        <p>Date</p>
        <p>Buyer Name</p>
        <p>Buyer Number</p>
      </div>
      {displayTranList}
      <div className="pagination">
        <div className="show-result">
          <p>
            Showing Result {pageVisited + 1} to{" "}
            {transactionsPerPage + pageVisited} of {total}
          </p>
        </div>
        <div className="pageNumber">
          <Paginate pageChange={pageChange} pageCount={pageCount} />
        </div>
      </div>
    </div>
  );
}

export default Transaction;
