import React,{useState,useEffect} from "react";
import dashData from "../../data/dashboard.json";
import "./dashboard.css";
import axios from "axios";
import {BASE_URL}from "../../util/constants"


function Dashboard() {

  const loadData=async()=>{
    try{
      const res = await axios.get(`${BASE_URL}/api/v1/report`)
    }catch(err){
      alert("Something went wrong")
    }
  }

  return (
    <div>
      {dashData.map((dashDetail, index) => {
        return (
          <div key={index}>
            <div className="dash-card">
              <div className="t-title">
                {" "}
                <p>{dashDetail.Name} Overview</p>
              </div>

              <div className="d-hr"></div>
              <div className="t-detail">
                <div className="t-cost t-card">
                  {" "}
                  <div className="t-img">
                    <img src="taka1.png" alt="taka" />
                  </div>
                  <div className="t-info">
                    <h3>Total Cost</h3>
                    <p>{dashDetail.totalCost} Taka</p>
                  </div>
                </div>
                <div className="t-revenue t-card">
                  {" "}
                  <div className="t-img">
                    <img src="taka2.png" alt="taka" />
                  </div>
                  <div className="t-info">
                    <h3>Total Revenue</h3>
                    <p>{dashDetail.totalRevenue} Taka</p>
                  </div>
                </div>
                <div className="t-profit t-card">
                  {" "}
                  <div className="t-img">
                    <img src="taka3.png" alt="taka" />
                  </div>
                  <div className="t-info">
                    <h3>Total Profit</h3>
                    <p>{dashDetail.totalProfit} Taka</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default Dashboard;
